DROP DATABASE zendb;
-- Create user and database
CREATE USER IF NOT EXISTS 'zen'@'%' IDENTIFIED BY 'zen';
GRANT ALL PRIVILEGES ON zendb.* TO 'zen'@'%' WITH GRANT OPTION;
FLUSH PRIVILEGES;

-- Create the database
CREATE DATABASE zendb CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE zendb;

-- Table: Categorie
CREATE TABLE categorie (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(100) NOT NULL UNIQUE
);

-- Table: Tipi di parola
CREATE TABLE tipi (
    id INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(50) NOT NULL UNIQUE
);

-- Table: Parole
CREATE TABLE parole (
    id INT AUTO_INCREMENT PRIMARY KEY,
    italiano VARCHAR(255) NOT NULL,
    inglese VARCHAR(255) NOT NULL,
    esempi TEXT, -- separati da ';' oppure JSON
    categoria_id INT NOT NULL,
    tipo_id INT NOT NULL,
    FOREIGN KEY (categoria_id) REFERENCES categorie(id) ON DELETE CASCADE,
    FOREIGN KEY (tipo_id) REFERENCES tipi(id) ON DELETE CASCADE
);

CREATE TABLE utente (
    id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(50) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL, 
    nome VARCHAR(100),
    cognome VARCHAR(100),
    email VARCHAR(100),
    cellulare VARCHAR(20),
    punteggio INT DEFAULT 0,
    livello INT DEFAULT 1,
    numero_accessi INT DEFAULT 0,
    tempo_online INT DEFAULT 0,
    data_nascita DATE
);



-- Creazione della tabella
CREATE TABLE frasi (
    id INT AUTO_INCREMENT PRIMARY KEY,
    frase_incompleta VARCHAR(255) NOT NULL,
    categoria_id INT NOT NULL,
    soluzione VARCHAR(50) NOT NULL
);


